import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Sparkles } from 'lucide-react';
import { generateTileKey, isTileRevealed } from '../utils/helpers';
import Tile from './Tile';

const GameBoard = ({ revealedTiles, backgroundImage, onTileClick, currentNextTile }) => {
  const gridSize = 4;

  return (
    <div className="relative bg-white/80 backdrop-blur-xl rounded-3xl shadow-2xl overflow-hidden max-w-2xl mx-auto border-4 border-pink-200/50 my-8">
      <img
        src={backgroundImage}
        alt="Imagen misteriosa de Roxi"
        className="absolute inset-0 w-full h-full object-cover opacity-25"
        style={{ filter: 'blur(6px)' }}
      />
      <div className="absolute inset-0 bg-gradient-to-br from-pink-500/15 to-rose-500/15" />

      <div 
        className="relative grid grid-cols-4 gap-3 p-6 aspect-square max-h-[600px] w-full" 
        style={{ gridTemplateRows: 'repeat(4, 1fr)' }}
      >
        {Array.from({ length: gridSize * gridSize }, (_, index) => {
          const row = Math.floor(index / gridSize);
          const col = index % gridSize;
          const key = generateTileKey(row, col);
          const isRevealed = isTileRevealed(revealedTiles, row, col);
          const isNext = currentNextTile && row === currentNextTile[0] && col === currentNextTile[1];

          return (
            <Tile
              key={key}
              row={row}
              col={col}
              isRevealed={isRevealed}
              isNext={isNext}
              onClick={!isRevealed && isNext ? () => onTileClick(row, col) : undefined}
              size="large"
            />
          );
        })}
      </div>

      {currentNextTile && (
        <motion.div
          className="absolute -bottom-4 left-1/2 transform -translate-x-1/2 bg-yellow-400 text-black px-4 py-2 rounded-full text-sm font-bold shadow-lg flex items-center gap-2"
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.3 }}
        >
          <Sparkles className="w-4 h-4" />
          Â¡Toca esta pieza para el reto!
        </motion.div>
      )}
    </div>
  );
};

export default GameBoard;
