import React from 'react';
import { motion } from 'framer-motion';
import { Sparkles, Star } from 'lucide-react';

const Tile = ({ row, col, isRevealed, isNext, onClick, size = 'normal' }) => {
  const sizeClass = size === 'large' ? 'w-full h-full' : '';

  return (
    <motion.div
      className={`relative bg-gradient-to-br from-pink-100/80 to-rose-200/80 border-4 border-pink-300/50 rounded-2xl cursor-pointer flex items-center justify-center transition-all duration-300 shadow-xl ${sizeClass} ${
        isRevealed 
          ? 'bg-transparent border-transparent shadow-none' 
          : isNext 
            ? 'border-yellow-400/80 bg-yellow-50/90 shadow-yellow-300 ring-4 ring-yellow-200/50 animate-pulse' 
            : 'hover:border-rose-500/80 hover:shadow-2xl'
      }`}
      onClick={onClick}
      initial={false}
      whileHover={!isRevealed && !!onClick ? { scale: 1.08 } : {}}
      whileTap={!isRevealed && !!onClick ? { scale: 0.92 } : {}}
      style={{ perspective: 1000, transformStyle: 'preserve-3d' }}
    >
      {!isRevealed ? (
        <motion.div
          className={`absolute inset-0 rounded-2xl bg-gradient-to-br from-pink-200/95 to-rose-300/95 flex items-center justify-center backdrop-blur-md ${sizeClass}`}
          style={{ backfaceVisibility: 'hidden' }}
          animate={isNext ? { scale: [1, 1.08, 1], rotate: [0, 8, -8, 0] } : {}}
          transition={{ duration: 1.5, repeat: Infinity }}
        >
          {isNext ? (
            <Star className="w-10 h-10 md:w-12 md:h-12 text-yellow-500 animate-bounce" />
          ) : (
            <Sparkles className="w-8 h-8 md:w-10 md:h-10 text-pink-500 animate-pulse" />
          )}
        </motion.div>
      ) : (
        <motion.div
          className="absolute inset-0 bg-gradient-to-br from-green-100/70 to-emerald-100/70 rounded-2xl flex items-center justify-center"
          style={{ 
            backfaceVisibility: 'hidden', 
            transform: 'rotateY(180deg)',
            boxShadow: 'inset 0 0 30px rgba(34, 197, 94, 0.4)' 
          }}
          animate={{ rotateY: [180, 360] }}
          transition={{ duration: 0.8, ease: 'easeOut' }}
        >
          <Sparkles className="w-6 h-6 md:w-8 md:h-8 text-emerald-500 animate-ping" />
        </motion.div>
      )}
    </motion.div>
  );
};

export default Tile;
